	public static void loadConfig(ForgeConfigSpec spec, File dir, String toml) {
		Path path = dir.toPath().resolve(toml);
		CommentedFileConfig configData = CommentedFileConfig.builder(path)
				.sync().autosave().preserveInsertionOrder().build();
		loadConfig(spec, configData);
	}
	
	public static void loadConfig(ForgeConfigSpec spec, CommentedFileConfig configData) {
		configData.load();
		spec.setConfig(configData);
	}
	
	public static Map<String, Map<String, Integer>> buildMaps(List<String> biomes, List<String> prob, List<String> minGroup, List<String> maxGroup) {
		Map<String, Map<String, Integer>> full = new HashMap<String, Map<String, Integer>>();
		
		if (biomes != null) {
			int[] probList = ObjectTranslators.getIntArray(prob.toArray(new String[0]));
			int[] minGroupList = ObjectTranslators.getIntArray(minGroup.toArray(new String[0]));
			int[] maxGroupList = ObjectTranslators.getIntArray(maxGroup.toArray(new String[0]));
			for (int i = 0; i < biomes.size(); i++) {
				Map<String, Integer> vars = new HashMap<String, Integer>();
				vars.put("probability", probList[i]);
				vars.put("min_group", minGroupList[i]);
				vars.put("max_group", maxGroupList[i]);
				full.put(biomes.get(i), vars);
			}
		}
		
		return full;
	}
